            display: flex;
            align-items: center;
        }

        .igx-sparkline {
            margin-top: 20px;
            margin-bottom: -10px;
        }

        .s-overlay {
            display: flex;
            flex-direction: column;
            align-items: center;
            z-index: 10002;

            &__inner {
                @extend %flex-center;
                flex-direction: column;
                border-radius: rem(16px);
                min-width: rem(340px);
                overflow: hidden;
                background: #fff;
                box-shadow: 0 10px 20px rgba(0, 0, 0, .10), 0 6px 6px rgba(0, 0, 0, .16);
            }

            &__heading {
                @extend %flex-center;
                width: 100%;
                text-transform: uppercase;
                font-weight: bold;
                margin-bottom: rem(16px);
                margin-top: rem(24);
                font-size: rem(28px);
                height: rem(70px);
                padding: 0 rem(24px);
                letter-spacing: 2px;
                border-bottom: 1px solid #ddd;

                &--finish {
                    border-bottom: none;
                    margin-top: 0;
                    font-style: italic;
                    background-color: white;
                    background-image: linear-gradient(45deg, #222 25%, transparent 25%),
                    linear-gradient(-45deg, #222 25%, transparent 25%),
                    linear-gradient(45deg, transparent 75%, #222 75%),
                    linear-gradient(-45deg, transparent 75%, #222 75%);
                    background-size: 20px 20px;
                    background-position: 0 0, 0 10px, 10px -10px, -10px 0px;
                }

                > span {
                    padding: rem(8px);
                    background: #fff;
                    margin: 0 rem(8px);

                    > span {
                        margin: 0 rem(8px);
                    }
                }
            }

            &__trophy {
                display: flex;
                align-items: center;
                justify-content: center;
                position: relative;
                margin-bottom: rem(16px);

                img {
                    width: 40px;
                }

                span {
                    position: absolute;
                    top: 2px;
                    color: #fff;
                    text-shadow: 0 0 8px #444;
                    font-weight: bold;
                }
            }

            &__players {
                @extend %flex-center;
                padding: rem(24px);
            }

            &__player {
                @extend %flex-center;
                flex-direction: column;
                margin: 0 rem(32px);
                width: 120px;

                igx-avatar {
                    margin-bottom: rem(16px);
                }
                &--winner {
                    order: 2;
                    igx-avatar {
                        border: 4px solid #fbb13c;
                    }
                }
                &--second {
                    order: 1;
                    igx-avatar {
                        border: 4px solid #bdbdbd;
                    }
                }
                &--third {
                    order: 3;
                    igx-avatar {
                        border: 4px solid #b47845;
                    }
                }
            }

            &__player-name {
                color: color($color: 'primary', $variant: 800);
                font-size: 18px;
                margin: 0;
                white-space: nowrap;
            }
        }

        .s-overlay__player.s-overlay__player--winner {
            margin-bottom: rem(48px);
        }
    }
}

